type Cart {
  id: ID!
  email: String
  items: [Item!]!
  createdAt: DateTime!
  updatedAt: DateTime!
}

input CartCreateInput {
  email: String
}

type CartCreatePayload {
  cart: Cart
}

type Item {
  id: ID!
  product: Product!
  quantity: Int!
}

input ItemCreateInput {
  cartId: ID!
  productId: ID!
  quantity: Int!
}

input ItemUpdateInput {
  id: ID!
  quantity: Int!
}

type ItemCreatePayload {
  item: Item!
}

type ItemUpdatePayload {
  item: Item!
}

type ItemDeletePayload {
  id: ID!
  success: Boolean!
}

enum OrderStatus {
  CANCELLED
  CREATED
  FULFILLED
  PAID
}

type Order {
  id: ID!
  lines: JSON!
  totalAmount: Int!
  status: OrderStatus!
  createdAt: DateTime!
  updatedAt: DateTime!
}

type OrderConnection {
  items: [Order!]!
  totalCount: Int!
}

input OrderCreateInput {
  lines: JSON!
  totalAmount: Int!
}

input OrderUpdateInput {
  id: ID!
  status: OrderStatus!
}

type OrderCreatePayload {
  order: Order
}

type OrderUpdatePayload {
  order: Order
}

type Color {
  id: ID!
  name: String!
  createdAt: DateTime!
  updatedAt: DateTime!
}

type Review {
  id: ID!
  author: String!
  email: String!
  title: String!
  description: String!
  rating: Float!
  createdAt: DateTime!
  updatedAt: DateTime!
}

input ReviewCreateInput {
  productId: ID!
  author: String!
  email: String!
  title: String!
  description: String!
  rating: Float!
}

input ReviewOrderByInput {
  createdAt: Sort
}

type ReviewCreatePayload {
  review: Review
}

type Size {
  id: ID!
  name: String!
  createdAt: DateTime!
  updatedAt: DateTime!
}
